import styled from 'styled-components';

export const ChartContainer = styled.div`
    background-color: #3e3d42;
    padding: 20px;
    border-radius: 8px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
    
    h3 {
        margin-top: 0;
        color: #c8a971;
        text-align: center;
    }

    .chart-placeholder {
        width: 100%;
        height: 400px;
        background-color: #2a2a2e;
        display: flex;
        align-items: flex-end;
        justify-content: center;
        padding: 20px;
    }

    .candle-sample {
        display: flex;
        gap: 10px;
        align-items: flex-end;
        height: 80%;
    }

    .up-day, .down-day {
        width: 20px;
        position: relative;
    }

    .up-day {
        background-color: #00B746;
        height: 70%;
        &::before {
            content: '';
            position: absolute;
            top: 0;
            left: 50%;
            width: 2px;
            height: 20px;
            background-color: #00B746;
            transform: translate(-50%, -100%);
        }
        &::after {
            content: '';
            position: absolute;
            bottom: 0;
            left: 50%;
            width: 2px;
            height: 20px;
            background-color: #00B746;
            transform: translate(-50%, 100%);
        }
    }

    .down-day {
        background-color: #EF403C;
        height: 40%;
        &::before {
            content: '';
            position: absolute;
            top: 0;
            left: 50%;
            width: 2px;
            height: 20px;
            background-color: #EF403C;
            transform: translate(-50%, -100%);
        }
        &::after {
            content: '';
            position: absolute;
            bottom: 0;
            left: 50%;
            width: 2px;
            height: 20px;
            background-color: #EF403C;
            transform: translate(-50%, 100%);
        }
    }

    .current-price {
        margin-top: 10px;
        font-size: 18px;
        font-weight: bold;
        color: #c8a971;
        text-align: center;
    }
`;
