import React, { useEffect, useRef } from 'react';
import { createChart } from 'lightweight-charts';
import { ChartContainer } from './style';

const CandleChart = ({ symbol, currentPrice }) => {
    const chartContainerRef = useRef(null);
    const chartRef = useRef(null);
    const candleSeriesRef = useRef(null);

    useEffect(() => {
        if (!chartContainerRef.current) return;

        // Configuração do gráfico
        chartRef.current = createChart(chartContainerRef.current, {
            layout: {
                background: { color: '#2a2a2e' },
                textColor: '#c8a971',
            },
            grid: {
                vertLines: { color: '#3e3d42' },
                horzLines: { color: '#3e3a3e' },
            },
            width: chartContainerRef.current.clientWidth,
            height: 400,
        });

        candleSeriesRef.current = chartRef.current.addCandlestickSeries({
            upColor: '#00B746',
            downColor: '#EF403C',
            borderDownColor: '#EF403C',
            borderUpColor: '#00B746',
            wickDownColor: '#EF403C',
            wickUpColor: '#00B746',
        });

        const sampleData = [
            { time: '2023-01-01', open: 30.50, high: 32.80, low: 30.20, close: 32.45 },
            { time: '2023-01-02', open: 32.50, high: 33.20, low: 31.80, close: 32.10 },
            { time: '2023-01-03', open: 32.20, high: 32.90, low: 31.50, close: 32.70 },
            { time: '2023-01-04', open: 32.70, high: 33.50, low: 32.30, close: 33.20 },
            { time: '2023-01-05', open: 33.20, high: 33.80, low: 32.90, close: 33.50 },
        ];

        candleSeriesRef.current.setData(sampleData);

        const handleResize = () => {
            if (chartRef.current && chartContainerRef.current) {
                chartRef.current.applyOptions({ 
                    width: chartContainerRef.current.clientWidth 
                });
            }
        };

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
            if (chartRef.current) {
                chartRef.current.remove();
            }
        };
    }, []);

    return (
        <ChartContainer>
            <h3>{symbol} - Gráfico de Candlestick</h3>
            <div ref={chartContainerRef} className="chart-container" />
            <div className="current-price">
                Preço atual: R$ {currentPrice}
            </div>
        </ChartContainer>
    );
};

export default CandleChart;
