import React from 'react';
import { StockCardContainer, StockInfo, StockPrice, StockVariation, BuySellButtons } from './style';

export default function StockCard({ stock, onSelect, onBuySell, isSelected }) {
    const variationColor = stock.variation >= 0 ? '#28a745' : '#dc3545'; // Green for positive, red for negative

    return (
        <StockCardContainer onClick={() => onSelect(stock)} isSelected={isSelected}>
            <StockInfo>
                <h3>{stock.name} ({stock.symbol})</h3>
                <StockPrice>\$${stock.price.toFixed(2)}</StockPrice>
            </StockInfo>
            <StockVariation style={{ color: variationColor }}>
                {stock.variation >= 0 ? '+' : ''}{stock.variation.toFixed(2)}%
            </StockVariation>
            <BuySellButtons>
                <button onClick={(e) => { e.stopPropagation(); onBuySell('Comprar', stock); }}>Comprar</button>
                <button onClick={(e) => { e.stopPropagation(); onBuySell('Vender', stock); }}>Vender</button>
            </BuySellButtons>
        </StockCardContainer>
    );
}
