import styled from 'styled-components';

export const StockCardContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 15px;
    margin-bottom: 10px;
    border: 1px solid ${props => (props.isSelected ? '#007bff' : '#eee')};
    border-radius: 5px;
    background-color: ${props => (props.isSelected ? '#e7f3ff' : '#f9f9f9')};
    cursor: pointer;
    transition: all 0.2s ease-in-out;

    &:hover {
        background-color: ${props => (props.isSelected ? '#e7f3ff' : '#f0f0f0')};
        border-color: #007bff;
    }
`;

export const StockInfo = styled.div`
    h3 {
        margin: 0;
        font-size: 18px;
        color: #333;
    }

    p {
        margin: 5px 0 0;
        font-size: 14px;
        color: #666;
    }
`;

export const StockPrice = styled.span`
    font-size: 20px;
    font-weight: bold;
    color: #007bff;
`;

export const StockVariation = styled.span`
    font-size: 16px;
    font-weight: bold;
`;

export const BuySellButtons = styled.div`
    button {
        background-color: #007bff;
        color: white;
        border: none;
        padding: 8px 12px;
        border-radius: 4px;
        cursor: pointer;
        font-size: 14px;
        margin-left: 10px;
        transition: background-color 0.2s ease;

        &:hover {
            background-color: #0056b3;
        }

        &:first-child {
            background-color: #28a745; /* Buy button green */
            &:hover {
                background-color: #218838;
            }
        }

        &:last-child {
            background-color: #dc3545; /* Sell button red */
            &:hover {
                background-color: #c82333;
            }
        }
    }
`;
