import React, { useEffect, useRef } from 'react';
import { createChart, ColorType } from 'lightweight-charts';
import { useResizeDetector } from 'react-resize-detector';
import { ChartWrapper } from './style';

export default function CandlestickChart({ data }) {
    const chartContainerRef = useRef();
    const chartRef = useRef(null);
    const seriesRef = useRef(null);
    const { width, height, ref: resizeRef } = useResizeDetector();

    useEffect(() => {
        if (!chartContainerRef.current) return;

        const chart = createChart(chartContainerRef.current, {
            layout: {
                textColor: 'black',
                background: { type: ColorType.Solid, color: 'white' },
            },
            width: chartContainerRef.current.clientWidth,
            height: 400, // Initial height, will be adjusted by resize detector
            grid: {
                vertLines: {
                    color: '#e0e0e0',
                },
                horzLines: {
                    color: '#e0e0e0',
                },
            },
            timeScale: {
                timeVisible: true,
                secondsVisible: false,
            },
        });

        const candlestickSeries = chart.addCandlestickSeries({
            upColor: '#26a69a',
            downColor: '#ef5350',
            borderVisible: false,
            wickUpColor: '#26a69a',
            wickDownColor: '#ef5350',
        });

        chartRef.current = chart;
        seriesRef.current = candlestickSeries;

        return () => {
            if (chartRef.current) {
                chartRef.current.remove();
                chartRef.current = null;
            }
        };
    }, []);

    useEffect(() => {
        if (seriesRef.current && data.length > 0) {
            seriesRef.current.setData(data);
            if (chartRef.current) {
                chartRef.current.timeScale().fitContent();
            }
        }
    }, [data]);

    useEffect(() => {
        if (chartRef.current && width && height) {
            chartRef.current.applyOptions({ width, height: 400 }); // Keep height fixed or make it dynamic
            chartRef.current.timeScale().fitContent();
        }
    }, [width, height]);

    return (
        <ChartWrapper ref={resizeRef}>
            <div ref={chartContainerRef} style={{ width: '100%', height: '400px' }} />
        </ChartWrapper>
    );
}
